import React, { useRef, useEffect } from 'react';
import './Icons.css';
const useSearch = () => {
  const browser = useRef();
  const clickbrowser = () => browser.current.click();
  return { browser, clickbrowser };
};
const useClickIcon = () => {
  const icons = ['book', 'movie', 'calendar'];

  const [book, movie, calendar] = useRef();
  // const book = useRef();
  // const movie = useRef();
  // const calendar = useRef();
  const clickIcon = (icon) => (window.location.href = `./${icon}`);
  useEffect(() => {
    book.current.addEventListener('click', () => clickIcon('book'));
    movie.current.addEventListener('click', () => clickIcon('movie'));
    calendar.current.addEventListener('click', () => clickIcon('calendar'));
  }, []);
  return { book, movie, calendar };
};
const Icons = () => {
  const { browser, clickbrowser } = useSearch();
  const { book, movie, calendar } = useClickIcon();
  return (
    <div className="nav-icons">
      <span className="nav-icon book-icon" ref={book}>
        📚
      </span>
      <span className="nav-icon movie-icon" ref={movie}>
        🎥
      </span>
      <span className="nav-icon calendar-icon" ref={calendar}>
        🗓
      </span>
      <span>
        <input type="file" id="file-browser" ref={browser} />
        <input
          type="button"
          value="🔍"
          className="nav-icon search-icon"
          onClick={clickbrowser}
        />
      </span>
    </div>
  );
};

export default Icons;
