import React, { useEffect, useRef } from 'react';

const showSearch = (target) => {
  target.classList.toggle('invisible');
  const dropDown = document.querySelector('.dropDown-menu');
  //   console.log(d);
  target.addEventListener('keypress', (e) => {
    if (e.key === 'Enter') console.log('enter');
  });
  target.value = '';
};
export const SearchText = () => {
  const {
    target: { current },
  } = useRef();
  useEffect(() => {
    const searchBook = document.querySelector('.dropDown-book');
    const searchMovie = document.querySelector('.dropDown-movie');
    searchBook.addEventListener('click', () => showSearch(current));
    searchMovie.addEventListener('click', () => showSearch(current));
    return () => {
      searchBook.removeEventListener('click', () => showSearch(current));
      searchMovie.removeEventListener('click', () => showSearch(current));
    };
  });
  return (
    <input type="text" className="searchText invisible" ref={current}></input>
  );
};
