import React, { useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Icons.css';

const useSearch = () => {
  const browser = useRef();
  const clickbrowser = () => browser.current.click();
  return { browser, clickbrowser };
};
// const useClickIcon = () => {
