const Icons = () => {
  const { browser, clickbrowser } = useSearch();
  const { book, movie, calendar } = useClickIcon();
  return (
    <div className="nav-icons">
      <Link to="./book">
        <span className="nav-icon book-icon" ref={book}>
          📚
        </span>
      </Link>
      <span className="nav-icon movie-icon" ref={movie}>
        🎥
      </span>
      <span className="nav-icon calendar-icon" ref={calendar}>
        🗓
      </span>
      <span>
        <input type="file" id="file-browser" ref={browser} />
        <input
          type="button"
          value="🔍"
          className="nav-icon search-icon"
          onClick={clickbrowser}
        />
      </span>
    </div>
  );
};

export default Icons;
