import React, { useEffect } from 'react';
// import { LoadFunctionContext } from '../header/Search';
import { Link } from 'react-router-dom';
import { DeleteImage } from './DeleteImage';
import { Functions } from './Functions';

const getApi = () => {
  const api = 'https://openapi.naver.com/v1/search/book.json';
  console.log(api);
};

const deleteImg = () => {
  if (DeleteImage()) {
    Functions().loadImg();
  }
};

const BookSpace = () => {
  useEffect(() => {
    Functions().loadImg();
    const nBook = document.querySelector('.dropDown_book');
    nBook.addEventListener('click', getApi);
    return nBook.removeEventListener('click', getApi);
  }, []);

  return (
    <div className="book-div">
      <span className="edit-menu invisible">
        <Link
          to={{
            pathname: './memo',
            state: {
              category: 'book',
            },
          }}
        >
          <span className="memo-btn">✏️</span>
        </Link>
        <span className="delete-btn" onClick={deleteImg}>
          🗑
        </span>
      </span>
    </div>
  );
};

export default BookSpace;
