import { DeleteImage } from './DeleteImage';
import { Functions } from './Functions';

const useLoad = () => {
  // const loadFunction = useContext(LoadFunctionContext);
  // const [memo, setMemo] = useState('');
  const [id, setId] = useState(0);
  const content = document.querySelector('.contents-div');
  console.log(content);
  useEffect(() => {
    // loadFunction.loadImg();
    Functions().loadImg();
  }, []);
  // return { memo, setMemo, loadFunction };
};

const deleteImg = () => {
  if (DeleteImage()) {
    Functions().loadImg();
  }
};

const MovieSpace = () => {
  // const { memo, setMemo, loadFunction } = useLoad();
  useLoad();
  // const [id, setId] = useState(0);
  // const content = document.querySelector('.contents-div');
  // console.log(content);
  const clickedImg = document.querySelector('.clicked-img');
  let img_id = -1;
  if (clickedImg !== null) img_id = clickedImg.id;
  return (
    <div className="movie-div">
      {/* {id} */}
      <span className="edit-menu invisible">
        <Link
          to={{
            pathname: './memo',
            state: {
              category: 'movie',
              id: 999,
            },
          }}
        >
          <span
            className="memo-btn"
            onClick={(event) =>
              setId(
                parseInt(
                  event.target.parentNode.parentNode.parentNode.children[0].id
                )
              )
            }
          >
            ‚úèÔ∏è
          </span>
        </Link>
        <span className="delete-btn" onClick={deleteImg}>
          üóë
        </span>
      </span>
    </div>
  );
};

export default MovieSpace;
