import React, { useEffect, useRef } from 'react';

const showSearch = () => {
  const searchText = document.querySelector('.searchText');
  console.log(searchText);
  //   searchText.classList.remove('invisible');
};
export const SearchText = () => {
  const searchText = useRef();
  useEffect(() => {
    const searchBook = document.querySelector('.dropDown-book');
    const searchMovie = document.querySelector('.dropDown-movie');
    searchBook.addEventListener('click', showSearch);
    searchMovie.addEventListener('click', showSearch);
    return () => {
      searchBook.removeEventListener('click', showSearch);
      searchMovie.removeEventListener('click', showSearch);
    };
  });
  return (
    <input type="text" className="searchText" className="invisible"></input>
  );
};
