import React, { useState, useContext, useEffect } from 'react';
// import { LoadFunctionContext } from '../header/Search';
// import MemoSpace from './MemoSpace';
import { Link } from 'react-router-dom';
import { DeleteImage } from './DeleteImage';
import { Functions } from './Functions';

const useLoad = () => {
  // const loadFunction = useContext(LoadFunctionContext);
  // const [memo, setMemo] = useState('');
  useEffect(() => {
    // loadFunction.loadImg();
    const editMenu = document.querySelector('.edit-menu');
    const aaa = () => console.log('bsdf');
    editMenu.addEventListener('click', () => aaa);
    Functions().loadImg();
    return () => editMenu.removeEventListener('click', () => aaa);
  }, []);
  // return { memo, setMemo, loadFunction };
};

const deleteImg = () => {
  if (DeleteImage()) {
    Functions().loadImg();
  }
};

const MovieSpace = () => {
  // const { memo, setMemo, loadFunction } = useLoad();
  useLoad();
  const [id, setId] = useState(0);
  const clickedImg = document.querySelector('.clicked-img');
  let img_id = -1;
  if (clickedImg !== null) img_id = clickedImg.id;
  return (
    <div className="movie-div" id="-1">
      {id}
      <span className="edit-menu invisible">
        <Link
        // to={{
        //   pathname: './memo',
        //   state: {
        //     category: 'movie',
        //     id,
        //   },
        // }}
        >
          <span
            className="memo-btn"
            onClick={(event) =>
              setId(
                parseInt(
                  event.target.parentNode.parentNode.parentNode.children[0].id
                )
              )
            }
          >
            ✏️
          </span>
        </Link>
        <span className="delete-btn" onClick={deleteImg}>
          🗑
        </span>
      </span>
    </div>
  );
};

export default MovieSpace;
