import React, { useEffect } from 'react';
// import { LoadFunctionContext } from '../header/Search';
import { Link } from 'react-router-dom';
import { DeleteImage } from './DeleteImage';
import { Functions } from './Functions';
import axios from 'axios';

const getApi = async () => {
  // const key = '45fe65b5c3fbd3d400ad5daa0f415552';
  // const res = await axios.get('https://dapi.kakao.com/v3/search/book', {
  //   params: {
  //     query: '경제',
  //   },
  //   headers: {
  //     Authorization: 'KakaoAK ' + key,
  //   },
  // });
  // console.log(res.data.documents);
  const id = 'IbndeLcL_SuBUeSSKnlx';
  const sec = 'USIUkFnmuv';
  const naver = await axios.get('/v1/search/movie.json', {
    params: {
      query: '범죄',
    },
    headers: {
      Id: id,
      Secret: sec,
    },
  });
  console.log(naver);
};

const deleteImg = () => {
  if (DeleteImage()) {
    Functions().loadImg();
  }
};

const BookSpace = () => {
  useEffect(() => {
    Functions().loadImg();
    const nBook = document.querySelector('.dropDown-book');
    nBook.addEventListener('click', getApi);
    return () => nBook.removeEventListener('click', getApi);
  }, []);

  return (
    <div className="book-div">
      <span className="edit-menu invisible">
        <Link
          to={{
            pathname: './memo',
            state: {
              category: 'book',
            },
          }}
        >
          <span className="memo-btn">✏️</span>
        </Link>
        <span className="delete-btn" onClick={deleteImg}>
          🗑
        </span>
      </span>
    </div>
  );
};

export default BookSpace;
